tinymce.PluginManager.add('polymath', function(editor, url) {
    // Adds a menu item to the tools menu
    editor.addMenuItem('polymath', {
        text: 'Poly Math',
        context: 'insert',
        onclick: function() {
            // Open window with a specific url
            editor.windowManager.open({
                title: 'Poly Math Input',
                url: 'js/tinymce/plugins/polymath/mathdiag.html',
                width: 400,
                height: 600,
                buttons: [{
                    text: 'Close',
                    onclick: 'close'
                }]
            });
        }
    });
});